package com.juaracoding.looping;

public class looping3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1; i<5; i++) {
			for(int j=1; j<5; j++) {
				System.out.println(i);
			}
		}
		
		
		for(int i=1; 1<5; i++) {
			for(int j=4; j>=1; j--) {
				System.out.print("*");
			}
			System.out.println();
		}
		
		
	}

}

